sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var itemGR = new GlideRecord('x_1383184_charityb_item');
    if (itemGR.get(current.item_name)) {
        var yourBidAmount = current.your_bid_amount;

        // Check if the current bid is greater than the highest bid
        if (parseFloat(yourBidAmount) > parseFloat(itemGR.getValue('highest_bid_amount'))) {
            itemGR.setValue('highest_bid_amount', yourBidAmount);
            itemGR.update();

            // Update highest bid in bid record
//             current.highest_bid = yourBidAmount;
//             current.update();
        } 
    }

})(current, previous);
