    userGR.get(gs.getUserID());
    if (userGR.isValid()) {
        currentUserLocation = userGR.getValue('location'); // Assuming 'location' is the field containing country location in user profile
    }

    // 2. Add a query condition to filter items by the user's country location
    current.addQuery('donator.location', currentUserLocation); // Assuming 'donator' is the reference field to user profile table

