/************************************************************************************************/
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    var highestBidAmountStr = g_form.getValue('item_name.highest_bid_amount');
    var yourBidAmountStr = newValue;


    // Parse the currency values, remove any formatting if needed
    var highestBidAmount = parseCurrency(highestBidAmountStr);
    var yourBidAmount = parseCurrency(yourBidAmountStr);

    // Check if your_bid_amount is less than or equal to highest_bid_amount
    if (yourBidAmount <= highestBidAmount) {
        g_form.showFieldMsg('your_bid_amount', 'Your bid amount must be greater than the current highest bid amount.', 'error');
        g_form.setValue('your_bid_amount', '');
    } else {
        // Update the highest_bid_amount field
        g_form.setValue('highest_bid_amount', yourBidAmountStr);
    }
}

function parseCurrency(value) {
    // Remove any non-numeric characters (like currency symbols and commas)
    value = value.replace(/[^\d.-]/g, '');
    return parseFloat(value);
}
