sp_widget.client_script:
api.controller=(function() {
    data.widgetId = options.widgetId; 

    if (input && input.action === "create_profile") {
        var name = input.name;
        var location = input.location;
        var bio = input.bio;
        var profileImage = input.profileImage; 


        var userProfileGR = new GlideRecord("x_1383184_charityb_user_profile");

        // Set field values
        userProfileGR.setValue("name", name);
        userProfileGR.setValue("location", location);
        userProfileGR.setValue("bio", bio);


        if (profileImage) {
            userProfileGR.setValue("profile_image", profileImage);
        }


        var profileSysId = userProfileGR.insert();


        gs.addInfoMessage("User profile created successfully.");
        action.setRedirectURL(current);
        return;
    }
})();

sp_widget.script:
var FetchUserProfileAjax = Class.create();
FetchUserProfileAjax.prototype = Object.extendsObject(AbstractAjaxProcessor, {

    fetchUserProfile: function() {
        var userId = gs.getUserID();
        var userProfileGR = new GlideRecord("sys_user");
        if (userProfileGR.get(userId)) {
            var name = userProfileGR.getValue("name");
            var location = userProfileGR.getValue("location");

            var profileData = {
                name: name,
                location: location
            };

            return JSON.stringify(profileData);
        } else {
            return null; 
        }
    }

});
