sp_widget.client_script:
// Define the 'api' object
var api = {};

// Controller function
api.controller = function($scope) {
    var c = this;

    // Initialize data.events
    c.data.events = c.data.events || [];

    // Function to handle adding items for auction
    c.addItemsForAuction = function(event) {
        alert('Adding items for auction for event: ' + event.event_name);
        // Add your logic here to open a form for adding items for auction for the selected event
    };

    // Function to handle donating money directly
    c.donateMoney = function(event) {
        alert('Donating money directly to event: ' + event.event_name);
        // Add your logic here to open a form for donating money directly to the selected event
    };

    // Function to show event details
    c.showDetails = function(event) {
        c.selectedEvent = event;
    };
};

