sp_widget.script:
(function() {
    // Initialize an array to hold the events
    data.events = [];

    // Define the table name
    var tableName = 'x_1383184_charityb_events';  // Replace with your actual table name

    // Create a new GlideRecord object
    var gr = new GlideRecord(tableName);
    if (gr.isValid()) {
        gr.query();

        // Loop through each record and add it to the events array
        while (gr.next()) {
            data.events.push({
                event_name: gr.getValue('event_name'),
                event_manager: gr.getValue('event_manager'),
                start_date: gr.getValue('start_date'),
                end_date: gr.getValue('end_date'),
                charity_company: gr.getValue('charity_company'),
                event_status: gr.getValue('event_status'),
                image: gr.getDisplayValue('image')
            });
        }
    } else {
        gs.error('Invalid table name: ' + tableName);
    }
})();
