sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Retrieve the associated item record
    var item = new GlideRecord('x_1383184_charityb_item');
    if (!item.get(current.item_name)) {
        gs.addErrorMessage('Invalid item reference.');
        current.setAbortAction(true);
        return;
    }

    // Check if the bid amount is higher than the start bid amount
    if (current.your_bid_amount < item.start_bid_amount) {
        gs.addErrorMessage('Bid amount must be greater than or equal to the start bid amount.');
        current.setAbortAction(true);
        return;
    }

    // Check if the bid amount is higher than the current highest bid
    if (current.your_bid_amount <= item.highest_bid_amount) {
        gs.addErrorMessage('Bid amount must be higher than the current highest bid.');
        current.setAbortAction(true);
        return;
    }

    // Update the highest bid on the item record
    item.highest_bid_amount = current.your_bid_amount;
    item.update();
})(current, previous);
