sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }


    var highestBidAmount =  parseFloat(g_scratchpad.highestBidAmount);
    var yourBidAmount = parseFloat(newValue);



    if (yourBidAmount <= highestBidAmount) {
        g_form.showFieldMsg('your_bid_amount', 'Your bid amount must be greater than the current highest bid amount.', 'error');
        g_form.setValue('your_bid_amount', 0);
    } else {
        g_form.setValue('highest_bid_amount', yourBidAmount); //set yourbidamount to highestbidamount
        g_scratchpad.highestBidAmount = yourBidAmount; 
    }
}
