sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    var currentBidAmount = g_form.getValue('current_bid_amount');
    if (parseFloat(newValue) <= parseFloat(currentBidAmount)) {
        g_form.showFieldMsg('your_bid_amount', 'Your bid amount must be greater than the current bid amount.', 'error');
        g_form.setValue('your_bid_amount', '');
    } else {
        g_form.hideFieldMsg('your_bid_amount', 'error');
    }
}
