sys_script_client.script:
function onLoad(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var FetchLocationSI = Class.create();
    FetchLocationSI.prototype = Object.extendsObject(AbstractAjaxProcessor, {
        getUserLocation: function(userId) {
            var location = '';
            var userGr = new GlideRecord('sys_user');
            if (userGr.get(userId)) {
                location = userGr.getValue('location');
            }
            return location;
        },

        type: 'FetchLocationSI'
    });


}
