sys_script_client.script:
var isUpdating = false;

function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    function parseCurrency(value) {
        if (!value) return NaN;
        // Remove any non-numeric characters except for periods and minus sign
        value = value.replace(/[^0-9.-]+/g, "");
        return parseFloat(value);
    }

    var yourBid = parseCurrency(g_form.getValue('your_bid_amount'));
    var startBid = parseCurrency(g_form.getValue('item_name.start_bid_amount'));
    var highestBid = parseCurrency(g_form.getValue('item_name.highest_bid_amount'));


    if (yourBid > startBid && yourBid > highestBid) {
		isUpdating = true; // Set the flag to avoid recursion
        g_form.setValue('item_name.highest_bid_amount', yourBid);
    } else {
// 		g_form.clearValue('your_bid_amount');

        g_form.showFieldMsg('your_bid_amount','Your bid should be greater than the highest bid and start bid','error');
		isUpdating = true; // Set the flag to avoid recursion
		g_form.clearValue('your_bid_amount');
		g_form.setValue('your_bid_amount', 'NULL');
    }
	g_form.clearMessages('your_bid_amount');
}
